/**
 * 1. Render songs
 * 2. Scroll top
 * 3. Play/ pause / seek
 * 4. CD rotate
 * 5. Next / Previous
 * 6. Random
 * 7. Next / Repeat when ended
 * 8. Active songs
 * 9. Scroll active song into view
 * 10. Play song when clicked
 */

const $ = document.querySelector.bind(document);
const $$ = document.querySelectorAll.bind(document);

const PLAYER_STORAGE_KEY = 'DUNGDUY'

const heading = $('header h2')
const cdThumb = $('.cd-thumb')
const audio = $('#audio')
const cd = $('.cd');
const playBtn = $('.btn-toggle-play');
const player = $('.player');
const progress = $('.progress');
const nextBtn = $('.btn-next');
const prevBtn = $('.btn-prev');
const randomBtn = $('.btn-random');
const repeatBtn = $('.btn-repeat');
const playlist = $('.playlist');


const app = {
    currentIndex: 0,
    isPlaying: false,
    isRandom: false,
    isRepeat: false,
    config: JSON.parse(localStorage.getItem(PLAYER_STORAGE_KEY)) || {},
    songs: [
        {
            name: "Oải Hương",
            singer: "Vincent",
            path: "./audio/OaiHuong.mp3",
            image: "data:image/jpeg;base64,"
          },
          {
            name: "Chờ Em Trong Đêm",
            singer: "The Men",
            path: "./audio/ChoEmTrongDem.mp3",
            image:
              "data:image/jpeg;base64,"
          },
          {
            name: "Cô Đơn Dành Cho Ai",
            singer: "NAL",
            path: "./audio/CoDonDanhChoAi.mp3",
            image: "data:image/jpeg;base64,"
          },
          {
            name: "Bên Trên Tầng Lầu",
            singer: "Tăng Duy Tân",
            path: "./audio/BenTrenTangLau.mp3",
            image:
              "data:image/jpeg;base64,"
          },
          {
            name: "Cứu Vãn Kịp KHông ",
            singer: "Vương Anh Tú",
            path: "./audio/CuuVanKipKhong.mp3",
            image:
              "data:image/jpeg;base64,"
          },
          {
            name: "Mây Hồng Đưa Lối",
            singer: "Datkka",
            path: "./audio/MayHongDuaLoi.mp3",
            image:
              "data:image/jpeg;base64,"
          },
          {
            name: "Waiting For You",
            singer: "MONO",
            path: "./audio/WaitingForYou.mp3",
            image:
              "data:image/jpeg;base64,"
          },
          {
            name: "Tình Thu Sao Hạ Buồn",
            singer: "Datkka",
            path: "./audio/TinhThuSaoHaBuon.mp3",
            image:"data:image/jpeg;base64,",
        }
        ],
        setConfig: function(key, value){
            this.config[key] = value;
            localStorage.setItem(PLAYER_STORAGE_KEY, JSON.stringify(this.config));
        },
        render: function() {
            const htmls = this.songs.map((song, index) => {
                return `<div class="song ${index === this.currentIndex ? 'active' : ''}" data-index="${index}">
                <div class="thumb" style="background-image: url('${song.image}')">
                </div>
                <div class="body">
                  <h3 class="title">${song.name}</h3>
                  <p class="author">${song.singer}</p>
                </div>
                <div class="option">
                  <i class="fas fa-ellipsis-h"></i>
                </div>
              </div>`
            })

            playlist.innerHTML = htmls.join('');
        },

        definceProperties: function(){
            Object.defineProperty(this, 'currentSong', {
                get: function(){
                    return this.songs[this.currentIndex];
                }
            })
        },
        handleEvent: function() {
            const _this = this;
            const cdWidth = cd.offsetWidth

            // xử lý CD quay
            const cdThumbAnimation = cdThumb.animate([
                {transform: 'rotate(360deg)'}
            ], {
                duration: 10000, //10 seconds,
                interations: Infinity,
            })
            cdThumbAnimation.pause()

            // Xử lý phóng to thu nhỏ CD 
            document.onscroll = function() {
                const scrollTop = window.scrollY || document.documentElement.scrollTop;
                const newCdWidth = cdWidth - scrollTop

                cd.style.width = newCdWidth > 0 ? newCdWidth + 'px' : 0;
                cd.style.opacity = newCdWidth / cdWidth;

            }

            // Xử lý khi Click Play
            playBtn.onclick = function() {
                if(_this.isPlaying) {
                    audio.pause()
                }else{
                   
                    audio.play()
                    
                
                }
            }

            // khi song được play
            audio.onplay = function() {
                _this.isPlaying = true;
                player.classList.add('playing')
                cdThumbAnimation.play()
            }        
            // khi song bị pause
            audio.onpause = function() {
                _this.isPlaying = false;
                player.classList.remove('playing')
                cdThumbAnimation.pause()
            }

            // Khi tiến độ bài hát thay đổi
            audio.ontimeupdate = function() {
                if(audio.duration){
                    const progressPercent = Math.floor( audio.currentTime / audio.duration * 100)
                    progress.value = progressPercent 
                }
            }

            // xử lý khi tua song
            progress.onchange = function(e) {
                const seekTime = audio.duration /100 * e.target.value
                audio.currentTime = seekTime
            }

            // khi next bài hát
            nextBtn.onclick = function(){
                if(_this.isRandom){
                    _this.playRandomSong()
                }else{
                _this.nextSong()
            }
                audio.play()
                _this.render()
                _this.scrollToActiveSong()
            }

            // KHi lùi bài hát
            prevBtn.onclick = function(){
                if(_this.isRandom){
                    _this.playRandomSong()
                }else{
                _this.prevSong()
            }
                audio.play()
                _this.render()
                _this.scrollToActiveSong()
            }

            // xử lý bật tắt radom
            randomBtn.onclick = function(e){ 
                _this.isRandom = !_this.isRandom
                _this.setConfig('isRandom', _this.isRandom)
                randomBtn.classList.toggle("active", _this.isRandom)
            }

            // xử lý lặp lại một songs
            repeatBtn.onclick = function(e){
                _this.isRepeat = !_this.isRepeat
                _this.setConfig('isRepeat', _this.isRepeat)

                repeatBtn.classList.toggle("active", _this.isRepeat)

            }

            // xử lý next song khi audio ended
            audio.onended = function(){
                if(_this.isRepeat){
                    audio.play()
                }else{
                nextBtn.click()
                }
            }

            // Lắng nghe hành vi click vào playlist
            playlist.onclick = function(e){
                console.log(2132)
                const songNode = e.target.closest('.song:not(.active)')
                if(songNode || !e.target.closest('.option')){
                    // xử lý khi click vào song
                    if(songNode){
                        _this.currentIndex = Number(songNode.dataset.index)
                        _this.loadCurrentSong()
                        _this.render()
                        audio.play()
                    }

                    if(!e.target.closest('.option')){

                    }
                }
            }

        },
        scrollToActiveSong: function() {
            setTimeout(() =>{
                $('.song.active').scrollIntoView({
                    behavior: 'smooth',
                    block: 'nearest',  
                })
            }, 500)
        },

        loadConfig: function() {
            this.isRandom = this.config.isRandom;
            this.isRepeat = this.config.isRepeat;
        },
        loadCurrentSong: function() {
            // console.log(heading.innerHTML = )
            heading.textContent = this.currentSong.name;
           cdThumb.style.backgroundImage = `url('${this.currentSong.image}')`
            audio.src = this.currentSong.path

        },
        nextSong: function() {
            this.currentIndex++;
            if (this.currentIndex >= this.songs.length) {
                this.currentIndex = 0
            }
            this.loadCurrentSong()
        },
        prevSong: function() {
            this.currentIndex--;
            if (this.currentIndex < 0) {
                this.currentIndex = this.songs.length - 1
            }
            this.loadCurrentSong()
        },

        playRandomSong: function() {
            let newIndex
            do{
                newIndex = Math.floor(Math.random() * this.songs.length)
            }while(newIndex === this.currentIndex)
            this.currentIndex = newIndex
            this.loadCurrentSong()
        },
        start: function (){
            
            // gán cấu hình từ config vào ứng dụng
            this.loadConfig();
            // định nghĩa các thuộc tính cho object 
            this.definceProperties();

            // Lắng nghe / xử lý các sự kiện (DOM event)
            this.handleEvent();

            // Tải thông tin bài hát đầu tiên vào UI khi chạy ứng dụng
            this.loadCurrentSong();

            // Redenr playlist
            this.render();

            // hiển thị lại trạng thái ban đầu của button repeat và radom
            randomBtn.classList.toggle("active", this.isRandom)
            repeatBtn.classList.toggle("active", this.isRepeat)
        },

      
}
app.start()


